// ============================================
// IMPROVED JSON PARSING - HANDLES THINK OUTPUT
// ============================================

function safeJsonParse(jsonString, nodeName) {
  try {
    // Remove markdown code blocks
    let cleaned = jsonString
      .replace(/```json\s*/g, '')
      .replace(/```\s*/g, '')
      .trim();

    // Try direct parse first
    return JSON.parse(cleaned);
  } catch (e1) {
    // If fails, extract JSON from Think node output
    // Think nodes have text BEFORE the JSON object
    try {
      // Find first { and last }
      const firstBrace = jsonString.indexOf('{');
      const lastBrace = jsonString.lastIndexOf('}');

      if (firstBrace === -1 || lastBrace === -1) {
        throw new Error('No JSON object found in output');
      }

      const jsonOnly = jsonString.substring(firstBrace, lastBrace + 1);
      return JSON.parse(jsonOnly);
    } catch (e2) {
      console.error(`Failed to parse ${nodeName}:`, e2.message);
      console.error(`Output length:`, jsonString.length);
      console.error(`First 200 chars:`, jsonString.substring(0, 200));
      console.error(`Last 200 chars:`, jsonString.substring(jsonString.length - 200));
      throw new Error(`Failed to parse ${nodeName} output: ${e2.message}`);
    }
  }
}

// ============================================
// GET AGENT OUTPUTS
// ============================================

const agent1Raw = $('Story Analyzer').first().json.output;
const agent2Raw = $('STORY ARCHITECT').first().json.output;
const agent3Raw = $('CHAPTER TITLE GENERATOR').first().json.output;

console.log('Agent 1 output length:', agent1Raw.length);
console.log('Agent 2 output length:', agent2Raw.length);
console.log('Agent 3 output length:', agent3Raw.length);

// Parse safely
let coupleData, storyBlueprint, chapterTitles;

try {
  console.log('Parsing Agent 1...');
  coupleData = safeJsonParse(agent1Raw, 'Agent 1');
  console.log('Agent 1 parsed successfully');
} catch (e) {
  throw new Error('Agent 1 JSON parsing failed: ' + e.message);
}

try {
  console.log('Parsing Agent 2...');
  storyBlueprint = safeJsonParse(agent2Raw, 'Agent 2');
  console.log('Agent 2 parsed successfully');
} catch (e) {
  throw new Error('Agent 2 JSON parsing failed: ' + e.message);
}

try {
  console.log('Parsing Agent 3...');
  chapterTitles = safeJsonParse(agent3Raw, 'Agent 3');
  console.log('Agent 3 parsed successfully');
} catch (e) {
  throw new Error('Agent 3 JSON parsing failed: ' + e.message);
}

console.log('All agents parsed successfully');

// ============================================
// BUILD BASE CONTEXT (SHARED BY ALL CHAPTERS)
// ============================================

const baseContext = {
  // Partner 1 Information
  partner1_name: coupleData.basic_info?.partner1_name || "Partner 1",
  partner1_age: coupleData.basic_info?.partner1_age || 0,
  partner1_profession: coupleData.basic_info?.partner1_profession || "Unknown",
  partner1_traits: (coupleData.personalities?.partner1_traits || []).join(', '),
  partner1_quirks: (coupleData.personalities?.partner1_quirks || []).join(', '),
  partner1_fears: (coupleData.personalities?.partner1_fears || []).join(', '),

  // Partner 2 Information
  partner2_name: coupleData.basic_info?.partner2_name || "Partner 2",
  partner2_age: coupleData.basic_info?.partner2_age || 0,
  partner2_profession: coupleData.basic_info?.partner2_profession || "Unknown",
  partner2_traits: (coupleData.personalities?.partner2_traits || []).join(', '),
  partner2_quirks: (coupleData.personalities?.partner2_quirks || []).join(', '),
  partner2_fears: (coupleData.personalities?.partner2_fears || []).join(', '),

  // Relationship Basics
  relationship_length: coupleData.basic_info?.relationship_length || "Unknown",
  relationship_status: coupleData.basic_info?.relationship_status || "Dating",
  how_they_met: coupleData.meeting?.how || "Unknown",
  where_they_met: coupleData.meeting?.where || "Unknown",

  // Red Thread System (5 ELEMENTS)
  symbol_object: storyBlueprint.red_thread_system?.symbol_object?.item || "Unknown",
  symbol_significance: storyBlueprint.red_thread_system?.symbol_object?.significance || "",
  symbol_evolution: storyBlueprint.red_thread_system?.symbol_object?.how_it_evolves || "",

  recurring_location: storyBlueprint.red_thread_system?.recurring_location?.place || "Unknown",
  location_description: storyBlueprint.red_thread_system?.recurring_location?.description || "",
  location_significance: storyBlueprint.red_thread_system?.recurring_location?.why_significant || "",

  leitmotif_phrase: storyBlueprint.red_thread_system?.leitmotif_phrase?.phrase || "Unknown",
  phrase_context: storyBlueprint.red_thread_system?.leitmotif_phrase?.context || "",
  phrase_emotional_weight: storyBlueprint.red_thread_system?.leitmotif_phrase?.emotional_weight || "",

  running_gag: storyBlueprint.red_thread_system?.running_gag?.gag || "Unknown",
  gag_how_it_plays: storyBlueprint.red_thread_system?.running_gag?.how_it_plays || "",

  central_metaphor: storyBlueprint.red_thread_system?.central_metaphor?.metaphor || "Unknown",
  metaphor_evolution: storyBlueprint.red_thread_system?.central_metaphor?.evolution || "",

  // Story Conflicts
  primary_conflict: storyBlueprint.invented_elements?.primary_conflict?.conflict || "Unknown",
  conflict_why_authentic: storyBlueprint.invented_elements?.primary_conflict?.why_authentic || "",
  conflict_stakes: storyBlueprint.invented_elements?.primary_conflict?.stakes || "",

  secondary_conflicts: storyBlueprint.invented_elements?.secondary_conflicts || [],
  supporting_characters: storyBlueprint.invented_elements?.supporting_characters || [],

  emotional_stakes_partner1: storyBlueprint.invented_elements?.emotional_stakes?.partner1_fears || "",
  emotional_stakes_partner2: storyBlueprint.invented_elements?.emotional_stakes?.partner2_fears || "",

  // Character Arcs
  partner1_journey: storyBlueprint.character_arcs?.partner1_journey || "",
  partner2_journey: storyBlueprint.character_arcs?.partner2_journey || "",
  relationship_evolution: storyBlueprint.character_arcs?.relationship_evolution || "",
  theme: storyBlueprint.theme || "",

  // Real Story Elements
  interaction_style: coupleData.dynamics?.interaction_style || "",
  conflict_resolution_style: coupleData.dynamics?.conflict_style || "",
  what_makes_unique: coupleData.dynamics?.unique_aspect || "",
  key_moments: coupleData.story_elements?.key_moments || [],
  main_challenge_overcome: coupleData.story_elements?.main_challenge || "",
  inside_jokes: coupleData.red_thread_potential?.inside_jokes || [],
  traditions: coupleData.red_thread_potential?.traditions || []
};

console.log('Base context built successfully');

// ============================================
// HELPER FUNCTIONS
// ============================================

function getAct(chapterNum) {
  if (chapterNum <= 3) return "Act 1: Attraction";
  if (chapterNum <= 6) return "Act 2A: Connection";
  if (chapterNum <= 8) return "Act 2B: Crisis";
  return "Act 3: Resolution";
}

function getActDescription(chapterNum) {
  if (chapterNum <= 3) return "Meeting, sparks flying, deciding to try";
  if (chapterNum <= 6) return "Dating, falling in love, deepening bond";
  if (chapterNum <= 8) return "Major conflict, separation, pain";
  return "Fighting back, reconciliation, commitment";
}

function getTone(chapterNum) {
  if (chapterNum <= 3) return "Light, hopeful, discovering, anticipating";
  if (chapterNum <= 6) return "Romantic, intimate, deepening, warm";
  if (chapterNum <= 8) return "Dark, painful, tense, desperate";
  return "Hopeful, healing, triumphant, complete";
}

function getTimeJump(chapterNum) {
  if (chapterNum === 1) return "Story begins";
  if (chapterNum <= 3) return "Same day or next day";
  if (chapterNum <= 6) return "A few days later";
  if (chapterNum <= 8) return "Days or a week later";
  return "Shortly after previous events";
}

// ============================================
// CREATE 10 LOOP ITEMS (ONE PER CHAPTER)
// ============================================

const loopInput = [];

for (let i = 1; i <= 10; i++) {
  // Get chapter-specific data
  const chapterOutline = (storyBlueprint.chapter_summaries || []).find(ch => ch.chapter_number === i);
  const chapterTitle = (chapterTitles.chapter_titles || []).find(ch => ch.chapter_number === i);

  if (!chapterOutline) {
    console.warn(`Chapter ${i} outline not found - using defaults`);
  }

  if (!chapterTitle) {
    console.warn(`Chapter ${i} title not found - using defaults`);
  }

  loopInput.push({
    // Chapter Identification
    chapter_number: i,
    chapter_title: chapterTitle?.title || `Chapter ${i}`,

    // Chapter Requirements (from outline)
    chapter_main_event: chapterOutline?.what_happens || "Chapter content to be determined",
    target_emotional_beat: chapterOutline?.emotional_beat || "To be determined",
    red_thread_elements_usage: chapterOutline?.red_thread_elements || "All 5 elements",

    // Act Structure
    current_act: getAct(i),
    act_description: getActDescription(i),
    chapter_tone: getTone(i),
    time_jump: getTimeJump(i),

    // Shared Context (same for all chapters)
    base_context: baseContext,

    // Memory Placeholders (filled during loop by Simple Memory)
    previous_chapters_summaries: [],
    last_chapter_ending: "",
    partner1_current_emotional_state: i === 1 ? "Normal life, open to possibilities" : "",
    partner2_current_emotional_state: i === 1 ? "Normal life, open to possibilities" : "",
    relationship_current_status: i === 1 ? "Not yet met" : "",
    open_plot_threads: [],

    // Flags
    is_first_chapter: i === 1,
    is_final_chapter: i === 10,

    // Word Count Target
    target_word_count: 2500,
    word_count_tolerance: 25
  });
}

console.log(`Successfully created ${loopInput.length} loop items`);

// ============================================
// RETURN FOR LOOP OVER ITEMS NODE
// ============================================

return loopInput.map(item => ({ json: item }));

// Helper function to clean text - MOST AGGRESSIVE: Single line breaks only
function cleanText(str) {
  if (!str) return '';

  return str
    .replace(/[\r\n]+/g, ' ')
    .replace(/ {2,}/g, ' ')
    .replace(/\\/g, '\\\\')
    .replace(/"/g, '\\"');
}

// Get all items from Loop Over Items
const allItems = $('Loop Over Items').all();

// Extract chapters
const chapters = allItems.map((item, index) => {
  const fullText = item.json.output || '';

  const titleMatch = fullText.match(/CHAPTER\s*\d+:\s*(.+?)[\n\r]/i);
  const title = titleMatch ? titleMatch[1].trim() : `Chapter ${index + 1}`;

  const content = fullText.replace(/CHAPTER\s*\d+:.*?[\n\r]+/i, '').trim();

  return { title, content };
});

// Return with cleaned content (NO page breaks)
return [{
  json: {
    chaptername1: chapters[0]?.title || '', 
    chapter1: cleanText(chapters[0]?.content || ''),
    chaptername2: chapters[1]?.title || '', 
    chapter2: cleanText(chapters[1]?.content || ''),
    chaptername3: chapters[2]?.title || '', 
    chapter3: cleanText(chapters[2]?.content || ''),
    chaptername4: chapters[3]?.title || '', 
    chapter4: cleanText(chapters[3]?.content || ''),
    chaptername5: chapters[4]?.title || '', 
    chapter5: cleanText(chapters[4]?.content || ''),
    chaptername6: chapters[5]?.title || '', 
    chapter6: cleanText(chapters[5]?.content || ''),
    chaptername7: chapters[6]?.title || '', 
    chapter7: cleanText(chapters[6]?.content || ''),
    chaptername8: chapters[7]?.title || '', 
    chapter8: cleanText(chapters[7]?.content || ''),
    chaptername9: chapters[8]?.title || '', 
    chapter9: cleanText(chapters[8]?.content || ''),
    chaptername10: chapters[9]?.title || '', 
    chapter10: cleanText(chapters[9]?.content || ''),
    total_chapters: 10
  }
}];
